import com.epam.trainings.spring.ioc.MessageGeneratorimport com.epam.trainings.spring.ioc.annotations.AnnotationHelloMessageGeneratorimport com.epam.trainings.spring.ioc.xml.XmlHelloMessageGeneratordef beans = {    def patronymic = 'Владимирович',            lastName = 'Емельяненко'    xmlHelloMessageGenerator (XmlHelloMessageGenerator, firstName: 'Фёдор', lastName: 'Емельяненко') {        patronymic = ref(patronymic)    }    annotationHelloMessageGenerator(AnnotationHelloMessageGenerator)    javaHelloMessageGenerator(MessageGenerator){        { -> "Hello, Фёдор $patronymic $lastName!" }    }}