package com.epam.trainings.spring.ioc.annotations;import com.epam.trainings.spring.ioc.HelloMessageGenerator;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Component;import javax.annotation.Resource;/** * @author Vyacheslav * @version 0.1 (02.04.2015). */@Componentpublic class AnnotationHelloMessageGenerator implements HelloMessageGenerator {    /* Аннотация "Resource" (часть базового Java API JSR-250, а не Spring`а) можно сказать, "трудолюбива" - она даёт    возможность задавать значение по-умолчанию в аннотации "Value", но она честно просматривает конфиги и если видит,    что в каком-либо из них задано соответствующее значение - вставляется оно. В данном случае в переменную вставится    значение "Василий" (раскомментируйте в конфиге строку <bean id="firstName" class="java.lang.String" c:_0="Фёдор"/> ,    что бы увидеть иной результат). */    @Resource    @Value("Василий")    @SuppressWarnings("SpringJavaAutowiringInspection")    private String firstName;    /* Здесь значение "Пупкин" будет проигнорировано, поскольку в конфиге задано иное значение - "Емельяненко":    <bean id="lastName" class="java.lang.String" c:_0="Емельяненко"/> */    @Resource    @Value("Пупкин")    private String lastName;    /* Аннотация "Autowired", напротив - "ленива": она ищет значение для подстановки лишь до тех пор, пока не найдёт    первое подходящее - и тогда его вставляет. Аннотация "Value" ближе, чем файл конфигурации, её Spring-сканнер видит    тут же, что называется, "не отходя от кассы", так что никуда больше не лезет и вставляет значение из неё.    Соответственно, здесь будет вставлено значение "Иванович" не смотря на то, что в конфиге есть строчка    <bean id="patronymic" class="java.lang.String" c:_0="Викторович"/> (закомментируйте аннотацию "Value"), что    бы увидеть иной результат. */    @Autowired    @Value("Иванович")    private String patronymic;    @Override    public String toString() {        return String.join(" ", firstName, patronymic, lastName);    }}