package com.epam.trainings.spring.ioc.java;import com.epam.trainings.spring.ioc.MessageGenerator;import org.springframework.beans.factory.BeanFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.*;/** * @author Vyacheslav Lapin (http://vlapin.ru/) * @version 0.1 (03.04.2015 17:07). */@Configuration@ComponentScan(basePackages = "com.epam.trainings.spring.ioc")@ImportResource("classpath:spring-config.xml")public class AppConfig {    @Autowired    private String lastName;    @Autowired    private String patronymic;    @Bean    public MessageGenerator javaHelloMessageGenerator() {        String s = String.format("Hello, Фёдор %s %s!", patronymic, lastName);        return () -> s;    }    public static void main(String... args) throws ClassNotFoundException {        BeanFactory beanFactory = new AnnotationConfigApplicationContext(                args.length > 0 ? Class.forName(args[0]) : AppConfig.class);        MessageGenerator messageGenerator = beanFactory.getBean("javaHelloMessageGenerator", MessageGenerator.class);        System.out.println(messageGenerator.getMessage());    }}