package ioc.java;import com.epam.trainings.spring.ioc.MessageGenerator;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.ImportResource;/** * @author Vyacheslav Lapin (http://vlapin.ru/) * @version 0.1 (03.04.2015 17:07). */@Configuration@ComponentScan(basePackages = "com.epam.trainings.spring.ioc")@ImportResource("classpath:application-context.xml")public class JavaConfig {    @Autowired    @SuppressWarnings("SpringJavaAutowiringInspection")    private String lastName;    @Autowired    @SuppressWarnings("SpringJavaAutowiringInspection")    private String patronymic;    @Bean    public MessageGenerator javaHelloMessageGenerator() {        String s = String.format("Hello, Фёдор %s %s!", patronymic, lastName);        return () -> s;    }}